import React, { useState, useEffect } from "react";
import { Button, View, Text, StyleSheet, ImageBackground, Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import CloudRegistration from './CloudRegistration';
import readme from '../assets/README.md';
import { MarkdownView } from 'react-native-markdown-view'


function AboutScreen() {

  const[file, setFile] = useState();



  return (
    <View style={styles.container}>
      <MarkdownView>
        # About the Project{'\n'}
        {'\n'}
        A sudoku app based on react native,  including hint, answer checking and highest score recording. Runs on both webpage or iOS.
        {'\n'}
        !(https://github.com/GodDamnGitHub/Sudoku/blob/CPA4/img/web.png" alt="image-20211112010551201){'\n'}
        !(https://github.com/GodDamnGitHub/Sudoku/blob/CPA4/img/web.png" alt="image-20211112010551201){'\n'}
        ### Built With{'\n'}
          * Use mongoDB to store user info 
          * Deploy a nodejs server to heroku to interact with the app
          * Login Interface
          * Personal and global high score 
          * Instead of question bank, there will be an algorithm to generate puzzles automatically 
          * Hint function   
          * Further beautify the UI
      </MarkdownView>    
    </View>
  );
}

export default AboutScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex-start',
    padding: 20,
  },
  p1: {
    flex: 2,
    padding: 20,
    borderWidth: 3
  },
});
